document.addEventListener("DOMContentLoaded",l=>{document.querySelectorAll(".toggle-title").forEach(t=>{const c=t.nextElementSibling,e=t.querySelector(".arrow");t.addEventListener("click",()=>{c.classList.toggle("hidden"),t.classList.toggle("active"),t.classList.contains("active")?e.innerHTML="&#9207;":e.innerHTML="&#9205;"})})});document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".clickable").forEach(l=>{l.addEventListener("click",function(n){n.preventDefault();const t=this.getAttribute("data-target");document.querySelectorAll(".row.special").forEach(e=>{e.style.display="none",e.querySelectorAll("img").forEach(s=>{s.classList.remove("show")})});const c=document.getElementById(t);c.style.display="flex",setTimeout(()=>{c.querySelectorAll("img").forEach(e=>{e.classList.add("show")})},10),document.querySelectorAll(".clickable").forEach(e=>{e.classList.remove("clicked")}),this.classList.add("clicked")})})});
